mapper.select:
SELECT reservation_id, room_id, user_id, start_at, end_at
FROM RESERVATIONS
WHERE room_id = #{roomId}
  AND start_at < #{endAt,jdbcType=TIMESTAMP}
  AND end_at > #{startAt,jdbcType=TIMESTAMP}
FOR UPDATE

SELECT reservation_id, room_id, user_id, start_at, end_at
FROM RESERVATIONS
WHERE reservation_id = #{reservationId}
